    "ActionMenu.simple": {
        "markup": "<table class=\"table-striped\" ng-controller=\"rxActionMenuCtrl\">\n  <thead>\n    <tr>\n      <th>Name</th>\n      <th class=\"actions\"></th>\n    </tr>\n  </thead>\n  <tbody>\n    <tr>\n      <td>Globally dismissible</td>\n      <td>\n        <rx-action-menu id=\"globalDismissal\">\n          <ul class=\"actions-area\">\n            <li>\n              <rx-modal-action\n                template-url=\"addActionTemplate.html\"\n                classes=\"msg-action\">\n                <i class=\"fa fa-plus fa-lg\"></i> Add\n              </rx-modal-action>\n            </li>\n            <li>\n              <rx-modal-action\n                template-url=\"deleteActionTemplate.html\"\n                classes=\"msg-warn\">\n                <i class=\"fa fa-times fa-lg\"></i> Delete\n              </rx-modal-action>\n            </li>\n          </ul>\n        </rx-action-menu>\n      </td>\n    </tr>\n    <tr>\n      <td>Only dismissible by clicking on cog</td>\n      <td>\n        <rx-action-menu global-dismiss=\"false\">\n          <ul class=\"actions-area\">\n            <li>\n              <rx-modal-action\n                template-url=\"addActionTemplate.html\"\n                classes=\"msg-action\">\n                <i class=\"fa fa-plus fa-lg\"></i> Add\n              </rx-modal-action>\n            </li>\n            <li>\n              <rx-modal-action\n                template-url=\"deleteActionTemplate.html\"\n                classes=\"msg-warn\">\n                <i class=\"fa fa-times fa-lg\"></i> Delete\n              </rx-modal-action>\n            </li>\n          </ul>\n        </rx-action-menu>\n      </td>\n    </tr>\n    <td>Unorthodox Behaviors (no modals, hidden item)</td>\n    <td>\n      <rx-action-menu id=\"custom\">\n        <ul class=\"actions-area\">\n          <li>\n            <button class=\"btn-link trigger\" ng-click=\"add()\">\n              <span class=\"msg-action\"><i class=\"fa fa-plus fa-lg\"></i> Add</span>\n            </button>\n          </li>\n          <li>\n            <button class=\"btn-link trigger\" ng-click=\"remove()\">\n              <span class=\"msg-warn\"><i class=\"fa fa-times fa-lg\"></i> Delete</span>\n            </button>\n          </li>\n          <li ng-show=\"false\">\n            <button class=\"btn-link trigger\">\n              <span class=\"msg-warn\"><i class=\"fa fa-times fa-lg\"></i> Visually Hidden</span>\n            </button>\n          </li>\n        </ul>\n      </rx-action-menu>\n    </td>\n  </tbody>\n</table>\n\n<script type=\"text/ng-template\" id=\"deleteActionTemplate.html\">\n    <rx-modal-form title=\"Delete Action\" submit-text=\"Delete\">\n        <span>Do you want to delete something?</span>\n    </rx-modal-form>\n</script>\n<script type=\"text/ng-template\" id=\"addActionTemplate.html\">\n    <rx-modal-form title=\"Add Action\" submit-text=\"Add\">\n        <span>Do you want to add something?</span>\n    </rx-modal-form>\n</script>\n",
        "javascript": "angular.module('demoApp')\n.controller('rxActionMenuCtrl', function ($scope, rxNotify) {\n    $scope.add = function () {\n        rxNotify.add('Added!', {\n            type: 'success',\n            repeat: false,\n            timeout: 3\n        });\n    };\n\n    $scope.remove = function () {\n        rxNotify.add('Deleted!', {\n            type: 'error',\n            repeat: false,\n            timeout: 3\n        });\n    };\n});\n"
    },
    "Breadcrumbs.simple": {
        "markup": "<div ng-controller=\"BreadcrumbsSimpleCtrl\">\n  <nav>\n    <rx-breadcrumbs></rx-breadcrumbs>\n  </nav>\n</div>\n",
        "javascript": "angular.module('demoApp')\n.controller('BreadcrumbsSimpleCtrl', function ($scope, rxBreadcrumbsSvc) {\n    rxBreadcrumbsSvc.set([{\n        path: '#/elements',\n        name: 'Elements',\n    }, {\n        name: '<strong>All Elements</strong>',\n        status: 'demo'\n    }]);\n});\n"
    },
    "button.animated": {
        "markup": "<div ng-controller=\"buttonAnimatedExampleCtrl\">\n  <rx-button\n     default-msg=\"Click here to see ellipses\"\n     toggle=\"status.loading\"\n     toggle-msg=\"Loading Ellipses\"\n     ng-click=\"clickMe()\"\n     disable=\"status.disable\">\n  </rx-button>\n</div>\n",
        "javascript": "angular.module('demoApp')\n.controller('buttonAnimatedExampleCtrl', function ($scope, $timeout) {\n    $scope.status = {\n        loading: false,\n        disable: false\n    };\n\n    $scope.clickMe = function () {\n        $scope.status.loading = true;\n        $timeout(function () {\n            $scope.status.loading = false;\n        }, 4000);\n    };\n});\n"
    },
